INSERT INTO `t_vod_combine` VALUES ('166', '141069', '巴啦啦小魔仙', 'tv_29553', 'tv', ';巴啦啦小魔仙;;巴啦啦小魔仙;', '李思捷', '廖景萱|孙圳|黄安仪', '内地;', '2008', '儿童;', 'http://tv.kanketv.com/image/tv/android/180x240/AC/AC96BAED20FCBFE229553.jpg', 'http://tv.kanketv.com/image/tv/web/120x160/AC/AC96BAED20FCBFE229553.jpg', 'http://tv.kanketv.com/image/tv/android/360x480/AC/AC96BAED20FCBFE229553.jpg', 'http://tv.kanketv.com/image/tv/android/720x960/AC/AC96BAED20FCBFE229553.jpg', '魔仙堡是一个魔法世界，由魔仙女王统领，只有魔仙才能学习魔法，而且要懂得利用魔法来帮助别人。可是心术不正的黑魔仙小月竟偷练黑魔法，夺去魔仙彩石，想夺取最高魔法的秘密。小蓝身为守护魔仙，奉命追寻魔仙彩石及黑魔仙的下落而来到人类世界。小蓝藏身于音乐盒内，偶然之中被一对小姐妹凌美琪、凌美雪带回家中，小蓝更成为凌家的保姆，与美琪、美雪开始了一段珍贵的友情。在魔仙女王的首肯下，小蓝教会了美琪、美雪魔法，让她们成为小魔仙，利用魔法助人。后来黑魔仙小月现身，还培养严莉莉成了小黑魔仙，这邪两种势力拉开了战幕。魔仙王子也来到人间，最后帮助小蓝她们战胜了黑魔仙小月和严莉莉。', '0', '0.00', '', '1', '0', '2016-12-16 15:32:00', '2016-12-16 16:07:55', '0', '0', null, '\0', '86561;155571;96899;', '22574;', '213849');
