INSERT INTO `t_vod_combine` VALUES ('183', '4258', '射雕英雄传 胡歌', 'tv_31865', 'tv', ';射雕英雄传 胡歌;射雕英雄传胡歌;', '李国立', '胡歌|林依晨|袁弘|刘诗诗', '内地;', '2008', '古装;爱情;武侠;', 'http://tv.kanketv.com/image/tv/android/180x240/0B/0B65A8CAFB99D10E31865.jpg', 'http://tv.kanketv.com/image/tv/web/120x160/0B/0B65A8CAFB99D10E31865.jpg', 'http://tv.kanketv.com/image/tv/android/360x480/0B/0B65A8CAFB99D10E31865.jpg', 'http://tv.kanketv.com/image/tv/android/720x960/0B/0B65A8CAFB99D10E31865.jpg', '    忠良之后、少年郭靖承母命从蒙古草原南下寻找杀父仇人，他偶遇桃花岛主“东邪黄药师的女儿黄蓉，郭靖天生淳朴，憨厚宽和，黄蓉美丽无比，蕙质兰心，两人一见倾心。他们结伴闯荡江湖，经历了无数的奇遇、凶险，许下了“聪明人和笨人永远在一起，一起生，一起死的誓言。  ', '0', '0.00', '', '1', '0', '2016-12-16 15:32:16', '2016-12-16 16:07:57', '0', '0', null, '\0', '9485;16617;16618;16970;', '5160;', '231321');
