INSERT INTO `t_vod_combine` VALUES ('186', '3287', '老狼请客', 'anime_165', 'anime', ';老狼请客;老狼请客 1980版;;老狼请客;老狼请客 1980版;', '阎善春|方轶群', '', '内地;', '1980', '益智;亲子;搞笑;经典;', 'http://ikanpic.kanketv.com/image/cartoon/android/180x240/1C/1C2135E1E351AEB1165.jpg', 'http://ikanpic.kanketv.com/image/cartoon/web/120x160/1C/1C2135E1E351AEB1165.jpg', 'http://ikanpic.kanketv.com/image/cartoon/android/360x480/1C/1C2135E1E351AEB1165.jpg', 'http://ikanpic.kanketv.com/image/cartoon/android/720x960/1C/1C2135E1E351AEB1165.jpg', '老狼炖了一大锅鲜美的鸡汤，请了他的好朋友狐狸和狗熊来作客。狡猾的狐狸先到了，馋嘴的他趁着老狼出外的时候偷偷的把一整锅鸡汤吃完了。眼看着老狼慢悠悠的走回来了，狐狸眼猪一转，计上心来。他摸了点鸡血到自己脸上，见了老狼就嚎啕大哭，说狗熊偷鸡吃时被他撞见，想杀人灭口。听了狐狸的哭诉，老狼怒不可遏，提起刀子就找狗熊报仇去了。狐狸这时又抢先找到狗熊，楚楚可怜的说，老狼请客其实是个陷阱，他想把他们两都宰了吃。吓坏了的狗熊这时看到老狼提把刀子怒气冲冲赶来了，吓得转身就跑。', '0', '6.30', '', '1', '0', '2016-12-16 15:32:19', '2016-12-16 16:07:58', '0', '0', null, '\0', '', '86861;0;', '231328');
