INSERT INTO `t_vod_combine` VALUES ('262', '23497', '上甘岭最长的43天', 'documentary_6316', 'documentary', ';上甘岭-最长的43天;上甘岭最长的43天;', '', '', '内地;', '2012', '军事;纪录;', 'http://ikanpic.kanketv.com/image/doc/android/180x240/44/44864265997B86A96316.jpg', 'http://ikanpic.kanketv.com/image/doc/web/120x160/44/44864265997B86A96316.jpg', 'http://ikanpic.kanketv.com/image/doc/android/360x480/44/44864265997B86A96316.jpg', 'http://ikanpic.kanketv.com/image/doc/android/720x960/44/44864265997B86A96316.jpg', '上甘岭战役是朝鲜战争后期僵持阶段的一次主要战役。战役由美国第9军发动，以争夺朝鲜中部金化郡五圣山南麓村庄上甘岭及其附近地区的控制权为主，属于“联合国军金化攻势的一部分。此役前后历时43天，在3.7平方公里的地区，共发射炮弹超过230万发，双方伤亡约3万人。此战在中美两国都产生了深远的影响。', '0', '0.00', '', '1', '0', '2016-12-16 15:33:33', '2016-12-16 15:33:33', '0', '0', null, '\0', '', '', '291937');
