INSERT INTO `t_vod_combine` VALUES ('334', '31402', '一九七九年邓小平访美', 'documentary_27902', 'documentary', ';一九七九年邓小平访美;', '', '', '内地;', '2015', '历史;人物;', 'http://tv.kanketv.com/image/doc/android/180x240/AF/AFA4D2662217BE8E27902.jpg', 'http://tv.kanketv.com/image/doc/web/120x160/AF/AFA4D2662217BE8E27902.jpg', 'http://tv.kanketv.com/image/doc/android/360x480/AF/AFA4D2662217BE8E27902.jpg', 'http://tv.kanketv.com/image/doc/android/720x960/AF/AFA4D2662217BE8E27902.jpg', '简介:1979年1月1日,中华人民共和国与美利坚合众国发表联合公报,宣布两国正式建立外交关系。这是西方发达国家中最后一个和中国建交的国家。1月28日至2月5日,时任中国国务院副总理的邓小平和夫人卓琳应美国总统卡特和夫人的邀请赴美进行了为期8天的正式访问,中美关系从此走上了一个新开端。在此次访问中,台湾特工在美国收买枪手,企图暗杀邓小平,但是,在中美合作的严密安保下,行程有惊无险。', '0', '0.00', '', '1', '0', '2016-12-16 15:34:33', '2016-12-16 15:34:33', '0', '0', null, '\0', '', '', '318657');
