INSERT INTO `t_vod_combine` VALUES ('715', '120125', '小丸子特别', 'anime_22259', 'anime', ';小丸子特别;', '', '', '其他;', '2000', '其他;;', 'http://ikanpic.kanketv.com/image/cartoon/android/180x240/5A/5A64A8D0E99DC0F122259.jpg', 'http://ikanpic.kanketv.com/image/cartoon/web/120x160/5A/5A64A8D0E99DC0F122259.jpg', 'http://ikanpic.kanketv.com/image/cartoon/android/360x480/5A/5A64A8D0E99DC0F122259.jpg', 'http://ikanpic.kanketv.com/image/cartoon/android/720x960/5A/5A64A8D0E99DC0F122259.jpg', '该作品是以作者的童年（1974年）生活为蓝本的故事，其中一事一物均充满着70年代的怀旧气息。故事发生的舞台是日本静冈县清水市（从2005年起成为静冈县静冈市清水区），那是一个不折不扣的美丽港都，是个交通便利、渔产丰富、又与富士山为邻的好地方。故事围绕着小丸子以及其家人和同学展开，有关于亲情、友谊等，或是一些生活趣事，但当中有笑有泪，令人回想起童年的稚气。最特别的，当在故事人物尴尬时的招牌表情，会突然出现在脸上的黑线，有时还会伴随着一阵寒风从头后吹过。动画中幽默的旁白也是一大特色。', '0', '0.00', '', '1', '0', '2016-12-16 15:38:52', '2016-12-16 15:38:52', '0', '0', null, '\0', '', '', '4150');
