INSERT INTO `t_vod_combine` VALUES ('831', '29227', '怒火英雄', 'tv_53162', 'tv', ';怒火英雄;;怒火英雄;', '毛卫宁|张彤', '王雷|邓家佳|霍政谚|乔乔|伍宇娟', '内地;', '2016', '谍战;年代;战争;爱情;军事;剧情;', 'http://tv.kanketv.com/image/tv/android/180x240/9D/9D1D4104EB0778DB53162.jpg', 'http://tv.kanketv.com/image/tv/web/120x160/9D/9D1D4104EB0778DB53162.jpg', 'http://tv.kanketv.com/image/tv/android/360x480/9D/9D1D4104EB0778DB53162.jpg', 'http://tv.kanketv.com/image/tv/android/720x960/9D/9D1D4104EB0778DB53162.jpg', '在上海孤岛期,日本特务石原秘密实施“以毒养战,以毒制华向中国倾销鸦片的“毒化政策。他巧妙利用上海滩帮会老大关秉义与袁家的世仇挑起帮会争斗,他便可顺利接管帮会为贩卖鸦片做掩护。袁家俩兄弟顿时陷入国恨和家仇、尊严和金钱之间的抉择。袁帅在地下党员薛立群的引导帮助下,认清石原阴谋诡计并决心参与抗争。但袁也却追求钱财和社会名利,成为石原贩毒帮凶,他一意孤行,还将阻碍自己的薛立群及母亲杨彩霞害死。袁帅发誓为其报仇,从此,兄弟成为被血缘纽带撕扯的宿敌。热血青年袁帅与誓死不贩毒的富家千金关爽组成代号“判官的扫毒秘密行动组,暗中打击鸦片工厂、销售点及运输渠道,诛杀卖国投敌的大毒枭;他俩故意示弱来麻痹袁也和石原,为此他们忍受着敌人无数次的凌辱,最终配合新四军主力摧毁敌人的鸦片运输专列。', '0', '8.00', '', '1', '0', '2016-12-16 15:40:05', '2016-12-16 16:08:58', '0', '0', null, '\0', '5553;1536;21763;5279;2145;', '22392;6271;', '443807');
