INSERT INTO `t_vod_combine` VALUES ('879', '29967', '国际艺苑', 'arts_500', 'arts', ';国际艺苑;', '', '', '内地;', '2012', '文艺;', 'http://ikanpic.kanketv.com/image/arts/android/180x240/B8/B8AE0D1F31651104500.jpg', 'http://ikanpic.kanketv.com/image/arts/web/120x160/B8/B8AE0D1F31651104500.jpg', 'http://ikanpic.kanketv.com/image/arts/android/360x480/B8/B8AE0D1F31651104500.jpg', 'http://ikanpic.kanketv.com/image/arts/android/720x960/B8/B8AE0D1F31651104500.jpg', '国际艺苑是一档国际艺术欣赏栏目。通过国际艺苑，观众可以了解到时尚的信息，欣赏名师的大作，聆听古典的音乐，感受时代的流行曲，欣赏经典的舞蹈，感受现代杂技魔术的魅力。国际艺苑的宗旨是：带领观众领略全球文化！', '0', '0.00', '', '1', '0', '2016-12-16 15:40:40', '2016-12-16 15:40:40', '0', '0', null, '\0', '', '', '4486');
INSERT INTO `t_vod_combine` VALUES ('881', '4902', '叶蓓爱幻想的双鱼座', 'documentary_12995', 'documentary', ';叶蓓 爱幻想的双鱼座;', '', '', '内地;', '2013', '人物;', 'http://tv.kanketv.com/image/doc/android/180x240/67/671F31754A75338612995.jpg', 'http://tv.kanketv.com/image/doc/web/120x160/67/671F31754A75338612995.jpg', 'http://tv.kanketv.com/image/doc/android/360x480/67/671F31754A75338612995.jpg', 'http://tv.kanketv.com/image/doc/android/720x960/67/671F31754A75338612995.jpg', '本期内容介绍：她是一个知性与感性并具的音乐人。她一个接受正统教育，却敢于背上包，迈开腿，全世界到处乱走的人。她是叶蓓，爱幻想的双鱼座，酷爱旅行的歌者，摄影狂热分子。十年来，旅行已经成为了她的一种生活习惯，有咖啡和音乐陪伴的旅途，是她的最爱。她说，我要用眼睛、用耳朵去感知这个世界。', '0', '0.00', '', '1', '0', '2016-12-16 15:40:41', '2016-12-16 15:40:41', '0', '0', null, '\0', '', '', '4489');
