INSERT INTO `t_vod_combine` VALUES ('1205', '34533', '地雷英雄传', 'tv_49611', 'tv', ';地雷英雄传;;地雷英雄传;', '杨小波|张东东|三丑|冷娃|陈跃东', '王新军|宁静|韩张|郑媛元|邱晔|尹泽强|李恰|朱瑞祥|张凯元|王昊|唐宁|程润坤|胡高峰|李冲|邱彬|宫臣峰|李波|付煣煊|周佳楠|刘东健|曹积强|陈玥', '内地;', '2015', '战争;军事;爱情;农村;剧情;', 'http://tv.kanketv.com/image/tv/android/180x240/5D/5D0C93BBA7B50EAE49611.jpg', 'http://tv.kanketv.com/image/tv/web/120x160/5D/5D0C93BBA7B50EAE49611.jpg', 'http://tv.kanketv.com/image/tv/android/360x480/5D/5D0C93BBA7B50EAE49611.jpg', 'http://tv.kanketv.com/image/tv/android/720x960/5D/5D0C93BBA7B50EAE49611.jpg', '这是一个爱闯祸、鲁莽的民兵队长,一步步蜕变为顶天立地大英雄的传奇故事,是平民百姓奋起反抗,驱逐日寇的血泪悲歌!1943年日本侵华战争处于相持阶段,敌人实施三光政策,对我华北地区疯狂扫荡。八路军大部队与日军殊死搏斗,以赵大胆为首的赵家庄民兵队却误把八路军埋下的地雷挖走,闯下大祸,民兵队面临解散。赵大胆见识到了地雷的威力,决意偷学造雷技术。一次意外,他与女匪王马飞燕成了欢喜冤家,得她冒死相助;分分合合,他同被鬼子灭了门的鸿运行少爷刘运果最终联手,带领着一帮普普通通的人民群众,造地雷,埋地雷,大展地雷术,为把侵略者赶出家园,协助八路军大部队完成任务,做出了不可磨灭的贡献,谱写了一曲人民战争的英雄赞歌……', '0', '7.90', '', '1', '0', '2016-12-16 15:44:02', '2016-12-16 16:09:24', '0', '0', null, '\0', '13548;910;218613;227415;227507;209130;209104;0;0;28276;250995;0;6675;20324;0;0;88867;0;0;0;0;0;', '188724;99357;207795;0;0;', '534085');
