INSERT INTO `t_vod_combine` VALUES ('1295', '575', '天天饮食', 'arts_531', 'arts', ';天天饮食;天天饮食1999;天天饮食2015;;天天饮食;天天饮食1999;天天饮食2015;', '刘仪伟|陈志峰', '', '内地;', '1999|90年代', '美食;生活;现代;服务;', 'http://ikanpic.kanketv.com/image/arts/android/180x240/AB/ABAF21F422648853531.jpg', 'http://ikanpic.kanketv.com/image/arts/web/120x160/AB/ABAF21F422648853531.jpg', 'http://ikanpic.kanketv.com/image/arts/android/360x480/AB/ABAF21F422648853531.jpg', 'http://ikanpic.kanketv.com/image/arts/android/720x960/AB/ABAF21F422648853531.jpg', '天天饮食是中央电视台一个以介绍做菜方法、畅谈做菜体会为主要内容的知识性、趣味性、服务性栏目,节目每期介绍一道居家常吃的家常菜的做法,手法上打破了已往由专业厨师单纯传授烹饪技艺的传统节目形态,代之以主持人与观众边聊天边介绍的节目形式,场景设计也由传统的单一灶台转入了现代的厨房饭厅。首播时间:周一至周日06:00,独播频道:CCTV-1', '0', '8.60', '', '1', '0', '2016-12-16 15:45:12', '2016-12-16 16:09:33', '0', '0', null, '\0', '', '898;187530;', '573928');
