INSERT INTO `t_vod_combine` VALUES ('1299', '23485', '笑傲帮', 'arts_807868', 'arts', ';笑傲帮2016;笑傲帮;;笑傲帮2016;笑傲帮;', '百克力|陈辰', '百克力', '内地;', '2016', '搞笑;真人秀;娱乐;', 'http://tv.kanketv.com/image/arts/android/180x240/B9/B9862C854071774E807868.jpg', 'http://tv.kanketv.com/image/arts/web/120x160/B9/B9862C854071774E807868.jpg', 'http://tv.kanketv.com/image/arts/android/360x480/B9/B9862C854071774E807868.jpg', 'http://tv.kanketv.com/image/arts/android/720x960/B9/B9862C854071774E807868.jpg', '由《笑傲江湖》第二季原班人马打造的喜剧综艺《笑傲帮》将于每周五晚22:00在东方卫视开播。各喜剧新星电影小品双向发展，再度齐聚东方卫视。众多喜剧界新星，“欢喜冤家”刘亮白鸽、“冷面笑匠”孙建弘、“纸片人”逯爱岩、“百变星君”小沈龙、“哑剧老男孩”马朋、叶逢春等都将在节目中奉献笑料，更有主持界“吴彦祖”百克力前来助阵，节目想要不好笑都难。', '0', '8.50', '', '1', '0', '2016-12-16 15:45:16', '2016-12-16 16:09:33', '0', '0', null, '\0', '177540;', '177540;19026;', '573936');
INSERT INTO `t_vod_combine` VALUES ('1301', '13454', 'bbc之消失的古文明', 'documentary_25177', 'documentary', ';BBC Lost Cities Of The Ancients;消逝的古代文明;bbc之消失的古文明;', '', '', '英国;', '2006', '历史;文艺;', 'http://ikanpic.kanketv.com/image/doc/android/180x240/A8/A8F8679294F4AFE125177.jpg', 'http://ikanpic.kanketv.com/image/doc/web/120x160/A8/A8F8679294F4AFE125177.jpg', 'http://ikanpic.kanketv.com/image/doc/android/360x480/A8/A8F8679294F4AFE125177.jpg', 'http://ikanpic.kanketv.com/image/doc/android/720x960/A8/A8F8679294F4AFE125177.jpg', '古代部分最伟大的奇迹依旧埋藏在世人的脚底，没有被发现与探索。传奇的地方在几千年前消失。本节目以纪录片的手法，调查考古与历史线索的蛛丝马迹，将古代伟大的失落城市公诸于世。', '0', '7.80', ';BBC;', '1', '0', '2016-12-16 15:45:18', '2016-12-16 15:45:18', '0', '0', null, '\0', '', '', '573939');
