INSERT INTO `t_vod_combine` VALUES ('1542', '37802', '莫麟传奇之四大古国', 'anime_195', 'anime', ';莫麟传奇之四大古国;莫麟传奇之四大古国  2010版;;莫麟传奇之四大古国;莫麟传奇之四大古国  2010版;', '', '', '内地;', '2010', '冒险;益智;', 'http://tv.kanketv.com/image/cartoon/android/180x240/98/987AB29E3278DE07195.jpg', 'http://tv.kanketv.com/image/cartoon/web/120x160/98/987AB29E3278DE07195.jpg', 'http://tv.kanketv.com/image/cartoon/android/360x480/98/987AB29E3278DE07195.jpg', 'http://tv.kanketv.com/image/cartoon/android/720x960/98/987AB29E3278DE07195.jpg', '莫麟传奇之四大古国讲述的是海文星球的机器城市遭遇了千年以来最大的能源危机，平凡的初中学生莫麟因为意外事故成为了祖先天工口中的天选者，拥有了超级机器人的力量，于是，他被赋予了寻找新能源，保护能源转接成功的使命。为了不让自己的星球能源枯竭，莫麟和几个好朋友在与邪恶组织的斗智斗勇中一起寻找进入能源基地的线索，然后在能源基地内看到了地球四大文明古国的壮丽景观，也遇到了千奇百怪的守关者，渡过一个又一个扣人心弦的难关。莫麟传奇之四大古国更多视频请关注CNTV动画片台', '0', '0.00', '', '1', '0', '2016-12-16 15:48:04', '2016-12-16 15:48:04', '0', '0', null, '\0', '', '', '688170');
