INSERT INTO `t_vod_combine` VALUES ('2657', '49794', '龙门村的故事', 'tv_51499', 'tv', ';龙门村的故事;龙门村;;龙门村的故事;龙门村;', '彭澎|徐娜|杨波', '杨波|岳红|白雪|田成仁|肖剑|姜超|莫慧兰|高志强|马艺家|乌云其其格|牟洋|赵世林|洪涛|杨建伟|吴军', '内地;', '2016', '喜剧;农村;爱情;', 'http://tv.kanketv.com/image/tv/android/180x240/A3/A3A9D1905F0B448251499.jpg', 'http://tv.kanketv.com/image/tv/web/120x160/A3/A3A9D1905F0B448251499.jpg', 'http://tv.kanketv.com/image/tv/android/360x480/A3/A3A9D1905F0B448251499.jpg', 'http://tv.kanketv.com/image/tv/android/720x960/A3/A3A9D1905F0B448251499.jpg', '经济富庶、民风淳朴的龙门村在全省“最具传统底蕴文化村庄评比中以一票之差败北。为了在下次比赛中获胜,一群本对戏曲一窍不通的农民们使出浑身解数,展示十八般武艺,京剧、越剧、黄梅戏、豫剧,你方唱罢我登场,一场文化建设大竞赛红红火火地展开了,唱戏过程中闹出不少笑话,但村民们发现了其中的乐趣,更领略到了中国传统文化带来的精神享受,龙门村在第二年如愿以偿获得胜利,鞭炮声中,大家相约,为龙门村今后的文化发展继续努力.', '0', '7.80', '', '1', '0', '2016-12-16 15:56:32', '2016-12-16 16:10:41', '0', '0', null, '\0', '20207;1117;26529;5104;3057;0;6294;0;0;0;0;6942;3056;0;5190;', '0;0;20207;', '893131');
