INSERT INTO `t_vod_combine` VALUES ('2767', '24483', '名人面对面', 'documentary_7731', 'documentary', ';名人面对面2013;', '', '', '香港;', '2015', '访谈;', 'http://tv.kanketv.com/image/doc/android/180x240/58/58AEFC1F326641F47731.jpg', 'http://tv.kanketv.com/image/doc/web/120x160/58/58AEFC1F326641F47731.jpg', 'http://tv.kanketv.com/image/doc/android/360x480/58/58AEFC1F326641F47731.jpg', 'http://tv.kanketv.com/image/doc/android/720x960/58/58AEFC1F326641F47731.jpg', '郎平，奥运冠军，原中国排球队著名运动员、教练员，凭借强劲而精确的扣杀而赢得“铁榔头”绰号。曾入选中国体育劳伦斯奖第二届（1980年）十佳名单。1984年获全国“三八红旗手”。2012年3月3日，在2011-2012赛季全国女排联赛决赛中，带领广东恒大女排2比0战胜上海女排夺得冠军。', '0', '0.00', '', '1', '0', '2016-12-16 15:57:20', '2016-12-16 15:57:20', '0', '0', null, '\0', '', '', '904877');
