INSERT INTO `t_vod_combine` VALUES ('2769', '95985', '中国战法', 'arts_28243', 'arts', ';中国战法;', '杜平', '', '内地;', '2015', '国际;评论;轻松;军事;', 'http://tv.kanketv.com/image/arts/android/180x240/02/02F540552066D4B028243.jpg', 'http://tv.kanketv.com/image/arts/web/120x160/02/02F540552066D4B028243.jpg', 'http://tv.kanketv.com/image/arts/android/360x480/02/02F540552066D4B028243.jpg', 'http://tv.kanketv.com/image/arts/android/720x960/02/02F540552066D4B028243.jpg', '中国战法好比军事版的“锵锵三人行,每期时长23分钟,话题严肃而气氛轻松。中国战法由评论员杜平坐镇,邀请军事高级将领、高端智囊和各路军情豪杰,从与中国密切相关的军事事件、国际形势入手,分析他人未见之战况,点评他人未言之战法,批解中国如何在纷繁复杂之中步步为营,推开强国、强军的顶层设计。', '0', '0.00', '', '1', '0', '2016-12-16 15:57:22', '2016-12-16 16:10:46', '0', '0', null, '\0', '', '213904;', '904879');
